func TestBool(t *testing.T) {
	tests := []struct {
		Value bool
	}{
		{
			Value: true,
		},
		{
			Value: false,
		},
	}
	for i, tt := range tests {
		ttName := fmt.Sprintf("test %d", i)
		p := ptr.Bool(tt.Value)
		v := deref.Bool(p)
		if tt.Value != v {
			t.Errorf("%s: expected=%v, actual=%v", ttName, tt.Value, v)
		}
	}
}
